import React, { Component } from "react";
import "./ParkComponent.css";
import { Route, Link } from "react-router-dom";

class ParkComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      ParkImages: [],
      parkCode: "",
      park: ""
    };
  }
  componentDidMount() {
    let url =
      "https://developer.nps.gov/api/v1/parks?statecode=DC&api_key=y010UOulh2F9R7ahrlYpcyXUEJltY7vRei1uVxpe";
    //only loading json of DC museums because too many otherwise
    fetch(url)
      .then(res => res.json())
      .then(res => {
        this.setState({
          park: res.data[0].data,
          ParkImages: res.data[0].images,
          parkCode: res.data[0].parkCode,
          name: res.data[0].name
        });
      });
  }

  render() {
    let AllParks = this.state.park.map(park => {
      return (
        <div className="parkcomponent-container">
          <Link to={`park/${this.state.parkCode}`}>
            <p className="ParkName">{this.state.name}</p>
            <div className="ImageContainer">
              <div className="fade" />
              <img className="Image" src={this.state.ParkImages[0].url} />
            </div>
          </Link>
        </div>
      );
    });

    return <div>{AllParks}</div>;
  }
}

export default ParkComponent;
